describe("Big Power", function() {

    it("should be accurate for 7^1", function () {
        expect(bigPower( bigInt(7), bigInt(1))).toEqual(bigInt(7));
    });

    it("should be accurate for 7^2", function () {
        expect(bigPower( bigInt(7), bigInt(2))).toEqual(bigInt(49));
    });

    it("should be accurate for 7^5", function () {
        expect(bigPower( bigInt(7), bigInt(5))).toEqual(bigInt(16807));
    });

    it("should be accurate for 7^8", function () {
        expect(bigPower( bigInt(7), bigInt(8))).toEqual(bigInt(5764801));
    });

    it("should be accurate for 7^17", function () {
        expect(bigPower( bigInt(7), bigInt(17))).toEqual(bigInt(232630513987207));
    });

    it("should be accurate for 8^512", function () {
        expect(bigPower( bigInt(8), bigInt(512))).toEqual(bigInt(""));
    });

    it("should be accurate for 3^1024", function () {
        expect(bigPower( bigInt(3), bigInt(1024))).toEqual(bigInt(""));
    });

    it("should be accurate for 3^12345", function () {
        var libraryValue = bigInt(3).pow(bigInt(12345));
        var ourValue = bigPower(bigInt(3), bigInt(12345));
        var comparison = libraryValue.compare(ourValue);
        expect(comparison).toBe(0);
    });

});